		return body;
		};


  render() {
	return (
  	<div className="App">
    	<header className="App-header">
			{this.state.response.map(function(r,i){
				{	<h1>{r.nome}</h1>}
			})}

    	</header>
  	</div>
	);
  }
}

export default App;
